fn bench_local_simd_no_simd(c: &mut Criterion) {
    let seq = "";
    let mut sequence = seq.chars().collect::<Vec<char>>();
    sequence.insert(0, '$');

    let graph_struct = graph::read_graph(
        &"tests/DMA-3108.fa.353ea42.34ee7b1.1576367.smooth.fix.gfa",
        false,
    );

    let score_matrix = matrix::create_score_matrix_match_mis(2, -4);
    let hofp = HashMap::new();
    let mut scores_f32 = HashMap::new();
    for (k, v) in score_matrix.iter() {
        scores_f32.insert(*k, *v as f32);
    }
    let mut group = c.benchmark_group("Local POA");
    for i in [1, 2, 3] {
        unsafe {
            group.bench_with_input(BenchmarkId::new(" Simd", i), &i, |b, _i| {
                b.iter(|| {
                    local_poa::exec_simd(
                        black_box(&sequence),
                        black_box(("name", 0)),
                        black_box(&graph_struct),
                        black_box(&scores_f32),
                        false,
                        &hofp,
                    )
                })
            });

            group.bench_with_input(BenchmarkId::new("No Simd", i), &i, |b, _i| {
                b.iter(|| {
                    local_poa::exec(
                        black_box(&sequence),
                        black_box(("name", 0)),
                        black_box(&graph_struct),
                        black_box(&score_matrix),
                        false,
                        &hofp,
                    )
                })
            });
        }
    }
}

fn bench_global_simd_no_simd(c: &mut Criterion) {
    let seq = "";
    let mut sequence = seq.chars().collect::<Vec<char>>();
    sequence.insert(0, '$');

    let graph_struct = graph::read_graph(
        &"tests/DMA-3108.fa.353ea42.34ee7b1.1576367.smooth.fix.gfa",
        false,
    );

    let score_matrix = matrix::create_score_matrix_match_mis(2, -4);
    let hofp = HashMap::new();
    let mut scores_f32 = HashMap::new();
    for (k, v) in score_matrix.iter() {
        scores_f32.insert(*k, *v as f32);
    }
    let r_values = utils::set_r_values(
        &graph_struct.nwp,
        &graph_struct.pred_hash,
        graph_struct.lnz.len(),
    );
    let mut group = c.benchmark_group("Global abPOA");
    for i in [1, 2, 3] {
        unsafe {
            group.bench_with_input(BenchmarkId::new("Simd", i), &i, |b, _i| {
                b.iter(|| {
                    global_mk_abpoa::exec_simd(
                        black_box(&sequence),
                        black_box(("name", 0)),
                        black_box(&graph_struct),
                        black_box(&scores_f32),
                        1000,
                        false,
                        &hofp,
                        &r_values,
                    )
                })
            });

            group.bench_with_input(BenchmarkId::new("No Simd", i), &i, |b, _i| {
                b.iter(|| {
                    global_mk_abpoa::exec(
                        black_box(&sequence),
                        black_box(("name", 0)),
                        black_box(&graph_struct),
                        black_box(&score_matrix),
                        1000,
                        false,
                        &hofp,
                    )
                })
            });
        }
    }
}

fn bench_poa_eds(c: &mut Criterion) {
    let mut group = c.benchmark_group("Partial Order Alignment");
    let graph_struct = graph::read_graph(
        &"tests/DMA-3108.fa.353ea42.34ee7b1.1576367.smooth.fix.gfa",
        false,
    );
    let seqs = sequences::get_sequences(String::from("tests/dma-3108.fa"));
    let r_values = utils::set_r_values(
        &graph_struct.nwp,
        &graph_struct.pred_hash,
        graph_struct.lnz.len(),
    );

    let m = 2f32;
    let mm = -4f32;
    for (i, read) in seqs.0.iter().enumerate() {
        let seq = read.iter().map(|c| *c as u8).collect::<Vec<u8>>();
        unsafe {
            group.bench_with_input(BenchmarkId::new(" No Simd", i), &i, |b, _i| {
                b.iter(|| {
                    simd_poa::exec_no_simd(
                        black_box(&seq),
                        black_box(&graph_struct),
                        black_box(m),
                        black_box(mm),
                        0,
                    )
                })
            });

            group.bench_with_input(BenchmarkId::new(" Simd (avx2)", i), &i, |b, _i| {
                b.iter(|| {
                    simd_poa::exec(
                        black_box(&seq),
                        black_box(&graph_struct),
                        black_box(m),
                        black_box(mm),
                    )
                })
            });

            group.bench_with_input(BenchmarkId::new(" Simd and ab", i), &i, |b, _i| {
                b.iter(|| {
                    simd_abpoa_m_mm::exec(
                        black_box(&seq),
                        black_box(&graph_struct),
                        black_box(m),
                        black_box(mm),
                        5,
                        &r_values,
                        0,
                    )
                })
            });
        }
    }
    group.finish();
}

